				"uri": "/manual/hymns/the-spirit-of-god",
				"verses": [
					{
						"extra": false,
						"text": "The Spirit of God like a fire is burning!\nThe latter-day glory begins to come forth;\nThe visions and blessings of old are returning,\nAnd angels are coming to visit the earth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll sing and we&rsquo;ll shout with the armies of heaven,\nHosanna, hosanna to God and the Lamb!\nLet glory to them in the highest be given,\nHenceforth and forever, Amen and amen!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The Lord is extending the Saints&rsquo; understanding,\nRestoring their judges and all as at first.\nThe knowledge and power of God are expanding;\nThe veil o&rsquo;er the earth is beginning to burst.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll sing and we&rsquo;ll shout with the armies of heaven,\nHosanna, hosanna to God and the Lamb!\nLet glory to them in the highest be given,\nHenceforth and forever, Amen and amen!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll call in our solemn assemblies in spirit,\nTo spread forth the kingdom of heaven abroad,\nThat we through our faith may begin to inherit\nThe visions and blessings and glories of God.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll sing and we&rsquo;ll shout with the armies of heaven,\nHosanna, hosanna to God and the Lamb!\nLet glory to them in the highest be given,\nHenceforth and forever, Amen and amen!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "How blessed the day when the lamb and the lion\nShall lie down together without any ire,\nAnd Ephraim be crowned with his blessing in Zion,\nAs Jesus descends with his chariot of fire!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll sing and we&rsquo;ll shout with the armies of heaven,\nHosanna, hosanna to God and the Lamb!\nLet glory to them in the highest be given,\nHenceforth and forever, Amen and amen!",
						"type": "chorus"
					}
				]
