				"verses": [
					{
						"extra": false,
						"text": "We ever pray for thee, our prophet dear,\nThat God will give to thee comfort and cheer;\nAs the advancing years furrow thy brow,\nStill may the light within shine bright as now,\nStill may the light within shine bright as now.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We ever pray for thee with all our hearts,\nThat strength be given thee to do thy part,\nTo guide and counsel us from day to day,\nTo shed a holy light around our way,\nTo shed a holy light around our way.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We ever pray for thee with fervent love;\nAnd as the children&rsquo;s prayer is heard above,\nThou shalt be ever blest, and God will give\nAll that is meet and best while thou shalt live,\nAll that is meet and best while thou shalt live.",
						"type": "verse"
					}
				]
