				"verses": [
					{
						"extra": false,
						"text": "God of our fathers, we come unto thee,\nChildren of those whom thy truth has made free.\nGrant us the joy of thy presence today;\nNever from thee let us stray!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Never! Never!\nNever from thee let us stray!\nEver! Ever!\nEver to thee will we pray!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Grateful for all that thy bounty imparts,\nPraises we offer with voices and hearts.\nLife of our being, and sun of our day,\nNever from thee let us stray!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Never! Never!\nNever from thee let us stray!\nEver! Ever!\nEver to thee will we pray!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Blest with the gifts of the gospel of peace,\nDwelling in Zion, whose light shall increase,\nLed by the priesthood along the bright way,\nNever from thee will we stray!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Never! Never!\nNever from thee let us stray!\nEver! Ever!\nEver to thee will we pray!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Strengthened by thee for the conflict with sin,\nOnward we&rsquo;ll press till life&rsquo;s battle we&rsquo;ll win;\nThen in thy glory forever we&rsquo;ll stay;\nNever from thee will we stray!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Never! Never!\nNever from thee let us stray!\nEver! Ever!\nEver to thee will we pray!",
						"type": "chorus"
					}
				]
