				"verses": [
					{
						"extra": false,
						"text": "With all the pow&rsquo;r of heart and tongue,\nI&rsquo;ll praise my Maker in my song.\nAngels shall hear the notes I&rsquo;ll raise,\nApprove the song, and join the praise.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "I&rsquo;ll sing thy truth and mercy, Lord;\nI&rsquo;ll sing the wonders of thy word.\nNot all thy works and names below\nSo much thy pow&rsquo;r and glory show.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Amidst a thousand snares I stand,\nUpheld and guided by thy hand.\nThy words my fainting soul revive\nAnd keep my dying faith alive.",
						"type": "verse"
					}
				]
