				"verses": [
					{
						"extra": false,
						"text": "For all the Saints who from their labors rest,\nWho thee by faith before the world confessed,\nThy name, O Jesus, be forever blest.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Alleluia, Alleluia.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Oh, may thy soldiers, faithful, true, and bold,\n&mdash; Fight as the Saints who nobly fought of old,\nAnd win with them the victor&rsquo;s crown of gold.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Alleluia, Alleluia.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Thou art our rock, our fortress, and our might;\nThou, Lord, our captain in the well-fought fight;\nThou, in the darkness drear, our one true light.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Alleluia, Alleluia.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "And when the strife is fierce, the warfare long,\nSteals on the ear the distant triumph song,\nAnd hearts are brave again, and arms are strong.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Alleluia, Alleluia.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "From earth&rsquo;s wide bounds, from ocean&rsquo;s farthest coast,\nThrough gates of pearl streams in the countless host,\n&mdash; Singing to Father, Son, and Holy Ghost.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Alleluia, Alleluia.",
						"type": "chorus"
					}
				]
