				"uri": "/manual/hymns/great-god-attend-while-zion-sings",
				"verses": [
					{
						"extra": false,
						"text": "Great God, attend while Zion sings\nThe joy that from thy presence springs.\nTo spend one day with thee on earth\nExceeds a thousand days of mirth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Might I enjoy the meanest place\nWithin thy house, O God of grace,\nNo tents of ease or thrones of pow&rsquo;r\nShould tempt my feet to leave thy door.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "God is our sun; he makes our day.\nGod is our shield; he guards our way\nFrom all assaults of hell and sin,\nFrom foes without and fears within.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "All needful grace will God bestow\nAnd crown that grace with glory too.\nHe gives us all things and withholds\nNo blessings due to upright souls.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Our God, our King, whose sovereign sway\nThe glorious hosts of heav&rsquo;n obey\n(And devils at thy presence flee);\nBlest is the man that trusts in thee!",
						"type": "verse"
					}
				]
