				"verses": [
					{
						"extra": false,
						"text": "Nearer, dear Savior, to thee,\nNearer, nearer to thee&mdash;\nEver I&rsquo;m striving to be\nNearer, yet nearer to thee!\nTrusting, in thee I confide;\nHoping, in thee I abide.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Take, oh, take, and cherish me,\nNearer, dear Savior, to thee.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Nearer, dear Savior, to thee,\nNearer, nearer to thee&mdash;\nProved by my trials, I&rsquo;ll be\nNearer, yet nearer to thee!\nHumbly I come to thee now;\nEarnest, I prayerfully bow.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Take, oh, take, and cherish me,\nNearer, dear Savior, to thee.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Nearer, dear Savior, to thee,\nNearer, nearer to thee&mdash;\nEver my anthem will be\nNearer, yet nearer to thee!\nLoving thee, ever I pray,\nAid me thy will to obey.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Take, oh, take, and cherish me,\nNearer, dear Savior, to thee.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Nearer, dear Savior, to thee,\nNearer, nearer to thee&mdash;\nLet me by holiness be\nNearer, yet nearer to thee!\nWhen all my trials are done,\nWhen my reward I have won,",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Take, oh, take, and cherish me,\nNearer, dear Savior, to thee.",
						"type": "chorus"
					}
				]
