				"verses": [
					{
						"extra": false,
						"text": "Jesus, my Savior true,\nGuide me to thee.\nHelp me thy will to do.\nGuide me to thee.\nE&rsquo;en in the darkest night,\nAs in the morning bright,\nBe thou my beacon light.\nGuide me to thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Through this dark world of strife,\nGuide me to thee.\nTeach me a better life.\nGuide me to thee.\nLet thy redeeming pow&rsquo;r\nBe with me ev&rsquo;ry hour.\nBe thou my safety tow&rsquo;r.\nGuide me to thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "When strife and sin arise,\nGuide me to thee.\nWhen tears bedim my eyes,\nGuide me to thee.\nWhen hopes are crushed and dead,\nWhen earthly joys are fled,\nThy glory round me shed.\nGuide me to thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "When silent death draws near,\nGuide me to thee.\nCalm thou my trembling fear.\nGuide me to thee.\nLet me thy mercy prove.\nLet thy enduring love\nGuide me to heav&rsquo;n above.\nGuide me to thee.",
						"type": "verse"
					}
				]
