				"verses": [
					{
						"extra": false,
						"text": "How long, O Lord most holy and true,\nShall shadowed hope our joy delay?\nOur hearts confess, our souls believe\nThy truth, thy truth, thy light, thy will, thy way!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Thy truth has made our prison bright;\nThy light has dimmed the dying past.\nWe bend beneath thy loving will\nAnd seek thy onward, onward path at last.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Eternal Father, gentle Judge!\nSpeed on the day, redemption&rsquo;s hour.\nSet up thy kingdom; from thy house\nUnlock for us, for us the prison tow&rsquo;r.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "From grim confusion&rsquo;s awful depth\nThe wail of hosts, faith&rsquo;s urgent plea:\nRelease our anguished, weary souls;\nSwing wide, swing wide the gates, and set us free!",
						"type": "verse"
					}
				]
