				"verses": [
					{
						"extra": false,
						"text": "The witness of the Holy Ghost,\nAs borne by those who know,\nHas lifted me again to thee,\nO Father of my soul.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "I know that thou art in the heav&rsquo;n.\nI know the Savior reigns.\nI know a prophet speaks to us\nFor our eternal gain.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "My eyes are wet; my heart is full.\nThe Spirit speaks today.\nO Lord, wilt thou my life renew\nAnd in my bosom stay.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "As testimony fills my heart,\nIt dulls the pain of days.\nFor one brief moment, heaven&rsquo;s view\nAppears before my gaze.",
						"type": "verse"
					}
				]
