				"verses": [
					{
						"extra": false,
						"text": "On bended knees, with broken hearts,\nWe come before thee, Lord,\nIn secret and in open prayer&mdash;\nOh, wilt thou speak thy word?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Feed thou our souls, fill thou our hearts,\nAnd bless our fast, we pray,\nThat we may feel thy presence here\nAnd feast with thee today.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "We&rsquo;ve shared our bread with those in need,\nRelieved the suff&rsquo;ring poor.\nThe stranger we have welcomed in&mdash;\nWilt thou impart thy store?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Feed thou our souls, fill thou our hearts,\nAnd bless our fast, we pray,\nThat we may feel thy presence here\nAnd feast with thee today.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "As witnesses, we gather here\nTo thank, and to attest\nOf mercies and of miracles&mdash;\nOh, still our lives so bless!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Feed thou our souls, fill thou our hearts,\nAnd bless our fast, we pray,\nThat we may feel thy presence here\nAnd feast with thee today.",
						"type": "chorus"
					}
				]
