				"verses": [
					{
						"extra": false,
						"text": "In fasting we approach thee here\nAnd pray thy Spirit from above\nWill cleanse our hearts, cast out our fear,\nAnd fill our hunger with thy love.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Thru this small sacrifice, may we\nRecall that strength and life each day\nAre sacred blessings sent from thee&mdash;\nFill us with gratitude, we pray.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "And may our fast fill us with care\nFor all thy children now in need.\nMay we from our abundance share,\nThy sheep to bless, thy lambs to feed.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "This fast, dear Father, sanctify&mdash;\nOur faith and trust in thee increase.\nAs we commune and testify,\nMay we be filled with joy and peace.",
						"type": "verse"
					}
				]
