				"verses": [
					{
						"extra": false,
						"text": "Ere you left your room this morning,\nDid you think to pray?\nIn the name of Christ, our Savior,\nDid you sue for loving favor\nAs a shield today?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Oh, how praying rests the weary!\nPrayer will change the night to day.\nSo, when life gets dark and dreary,\nDon&rsquo;t forget to pray.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "When your heart was filled with anger,\nDid you think to pray?\nDid you plead for grace, my brother,\nThat you might forgive another\nWho had crossed your way?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Oh, how praying rests the weary!\nPrayer will change the night to day.\nSo, when life gets dark and dreary,\nDon&rsquo;t forget to pray.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "When sore trials came upon you,\nDid you think to pray?\nWhen your soul was full of sorrow,\nBalm of Gilead did you borrow\nAt the gates of day?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Oh, how praying rests the weary!\nPrayer will change the night to day.\nSo, when life gets dark and dreary,\nDon&rsquo;t forget to pray.",
						"type": "chorus"
					}
				]
