				"verses": [
					{
						"extra": false,
						"text": "In sweet remembrance of thy Son,\nWe gather in thy house as one\nTo join in prayer, to sing thy praise,\nTo worship thee and learn thy ways.\nFather, on this the Sabbath day,\nBe with us gathered here, we pray.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "And may our thoughts still turn to thee,\nWith loved ones, friends, and family.\nIn all we do till day is gone,\nMay worship still continue on.\nFather, on this the Sabbath day,\nBe with us in our homes, we pray.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Help each to seek a quiet hour\nTo read thy word and feel thy pow&rsquo;r,\nTo hear thy voice, though small and still,\nRenew our strength to do thy will.\nFather, on this the Sabbath day,\nBe with us in our hearts, we pray.",
						"type": "verse"
					}
				]
