				"verses": [
					{
						"extra": false,
						"text": "As the dew from heav&rsquo;n distilling\nGently on the grass descends\nAnd revives it, thus fulfilling\nWhat thy providence intends,",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Let thy doctrine, Lord, so gracious,\nThus descending from above,\nBlest by thee, prove efficacious\nTo fulfill thy work of love.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lord, behold this congregation;\nPrecious promises fulfill.\nFrom thy holy habitation\nLet the dews of life distill.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Let our cry come up before thee.\nThy sweet Spirit shed around,\nSo the people shall adore thee\nAnd confess the joyful sound.",
						"type": "verse"
					}
				]
