				"verses": [
					{
						"extra": false,
						"text": "O thou kind and gracious Father,\nReigning in the heav&rsquo;ns above,\nLook on us, thy humble children;\nFill us with thy holy love.\nFill us with thy holy love.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We have met this Sabbath morning,\nWords of life and truth to hear.\nTeach us how to ever serve thee\nAnd thy holy name revere,\nAnd thy holy name revere.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Help us to resist temptation;\nHelp us to refrain from ill.\nHelp us all to gain salvation;\nHelp us all to do thy will.\nHelp us all to do thy will.",
						"type": "verse"
					}
				]
