				"verses": [
					{
						"extra": false,
						"text": "We meet, dear Lord, in meekness all,\nResponding to thy Sabbath call\nTo gather here in thy blest name,\nAnd pledge our faithfulness again.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Thy counsels true we love to hear,\nAnd feel thy Spirit&rsquo;s presence near,\nAs we with friends our voices raise\nIn prayer and songs of joyous praise.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "In oneness and in love we come\nOnce more to this our Sabbath home,\nRejoicing in thy welcome here,\nUnto this place of peace and prayer.",
						"type": "verse"
					}
				]
