				"verses": [
					{
						"extra": false,
						"text": "Lord, we ask thee ere we part,\nBless the teachings of this day.\nPlant them deep in ev&rsquo;ry heart,\nThat with us they&rsquo;ll ever stay.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "In the innocence of youth,\nWe would all thy laws fulfill.\nLead us in the way of truth.\nGive us strength to do thy will.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Father, merciful and kind,\nWhile we labor for the right,\nMay we in thy service find\nSweetest pleasure, pure delight.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "All our follies, Lord, forgive.\nKeep us from temptations free.\nHelp us evermore to live\nLives of holiness to thee.",
						"type": "verse"
					}
				]
