				"verses": [
					{
						"extra": false,
						"text": "With humble heart, I bow my head\nAnd think of thee, O Savior, Lord.\nI take the water and the bread\nTo show remembrance of thy word.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Help me remember, I implore,\nThou gav&rsquo;st thy life on Calvary,\nThat I might live forevermore\nAnd grow, dear Lord, to be like thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "To be like thee! I lift my eyes\nFrom earth below toward heav&rsquo;n above,\nThat I may learn from vaulted skies\nHow I my worthiness can prove.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "As I walk daily here on earth,\nGive me thy Spirit as I seek\nA change of heart, another birth,\nAnd grow, dear Lord, to be like thee.",
						"type": "verse"
					}
				]
