				"verses": [
					{
						"extra": false,
						"text": "Father in Heav&rsquo;n, we do believe\nThe promise thou hast made;\nThy word with meekness we receive,\nJust as thy Saints have said.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We now repent of all our sin\nAnd come with broken heart,\nAnd to thy covenant enter in\nAnd choose the better part.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "O Lord, accept us while we pray,\nAnd all our sins forgive;\nNew life impart to us this day,\nAnd bid the sinners live.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Humbly we take the sacrament\nIn Jesus&rsquo; blessed name;\nLet us receive thru covenant\nThe Spirit&rsquo;s heav&rsquo;nly flame.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "We will be buried in the stream\nIn Jesus&rsquo; blessed name,\nAnd rise, while light shall on us beam\nThe Spirit&rsquo;s heav&rsquo;nly flame.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Baptize us with the Holy Ghost\nAnd seal us as thine own,\nThat we may join the ransomed host\nAnd with the Saints be one.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "For sacrament, sing verses 1&ndash;4.\nFor baptismal services, omit verse 4.",
						"type": "other"
					}
				]
