				"verses": [
					{
						"extra": false,
						"text": "He died! The great Redeemer died,\nAnd Israel&rsquo;s daughters wept around.\nA solemn darkness veiled the sky;\nA sudden trembling shook the ground.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Come, Saints, and drop a tear or two\nFor him who groaned beneath your load;\nHe shed a thousand drops for you,\nA thousand drops of precious blood.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Here&rsquo;s love and grief beyond degree;\nThe Lord of glory died for men.\nBut lo! what sudden joys were heard!\nThe Lord, though dead, revived again.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The rising Lord forsook the tomb.\nIn vain the tomb forbade him rise.\nCherubic legions guard him home\nAnd shout him welcome to the skies.",
						"type": "verse"
					}
				]
