				"verses": [
					{
						"extra": false,
						"text": "There is a green hill far away,\nWithout a city wall,\nWhere the dear Lord was crucified,\nWho died to save us all.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We may not know, we cannot tell,\nWhat pains he had to bear,\nBut we believe it was for us\nHe hung and suffered there.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "There was no other good enough\nTo pay the price of sin.\nHe only could unlock the gate\nOf heav&rsquo;n and let us in.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Oh, dearly, dearly has he loved!\nAnd we must love him too,\nAnd trust in his redeeming blood,\nAnd try his works to do.",
						"type": "verse"
					}
				]
