				"verses": [
					{
						"extra": false,
						"text": "Away in a manger, no crib for his bed,\nThe little Lord Jesus laid down his sweet head;\nThe stars in the heavens looked down where he lay,\nThe little Lord Jesus, asleep on the hay.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The cattle are lowing; the poor baby wakes,\nBut little Lord Jesus, no crying he makes.\nI love thee, Lord Jesus; look down from the sky\nAnd stay by my cradle till morning is nigh.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Be near me, Lord Jesus; I ask thee to stay\nClose by me forever, and love me, I pray.\nBless all the dear children in thy tender care,\nAnd fit us for heaven to live with thee there.",
						"type": "verse"
					}
				]
