				"verses": [
					{
						"extra": false,
						"text": "Hark! the herald angels sing\nGlory to the newborn King!\nPeace on earth and mercy mild,\nGod and sinners reconciled!\nJoyful, all ye nations, rise;\nJoin the triumph of the skies;\nWith th&rsquo;angelic host proclaim\nChrist is born in Bethlehem!",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hark! the herald angels sing\nGlory to the newborn King!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Hail the heav&rsquo;n-born Prince of Peace!\nHail the Son of Righteousness!\nLight and life to all he brings,\nRis&rsquo;n with healing in his wings.\nMild he lays his glory by,\nBorn that man no more may die;\nBorn to raise the sons of earth,\nBorn to give them second birth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hark! the herald angels sing\nGlory to the newborn King!",
						"type": "chorus"
					}
				]
