				"verses": [
					{
						"extra": false,
						"text": "With wond&rsquo;ring awe the wisemen saw\nThe star in heaven springing,\nAnd with delight, in peaceful night,\nThey heard the angels singing:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hosanna, hosanna, hosanna to his name!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "By light of star they traveled far\nTo seek the lowly manger,\nA humble bed wherein was laid\nThe wondrous little Stranger.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hosanna, hosanna, hosanna to his name!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "And still is found, the world around,\nThe old and hallowed story,\nAnd still is sung in ev&rsquo;ry tongue\nThe angels&rsquo; song of glory:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hosanna, hosanna, hosanna to his name!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The heav&rsquo;nly star its rays afar\nOn ev&rsquo;ry land is throwing,\nAnd shall not cease till holy peace\nIn all the earth is growing.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hosanna, hosanna, hosanna to his name!",
						"type": "chorus"
					}
				]
