				"verses": [
					{
						"extra": false,
						"text": "Far, far away on Judea&rsquo;s plains,\nShepherds of old heard the joyous strains:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Glory to God, \nGlory to God,\nGlory to God in the highest;\nPeace on earth, goodwill to men;\nPeace on earth, goodwill to men!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Sweet are these strains of redeeming love,\nMessage of mercy from heav&rsquo;n above:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Glory to God,\nGlory to God,\nGlory to God in the highest;\nPeace on earth, goodwill to men;\nPeace on earth, goodwill to men!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Lord, with the angels we too would rejoice;\nHelp us to sing with the heart and voice:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Glory to God,\nGlory to God,\nGlory to God in the highest;\nPeace on earth, goodwill to men;\nPeace on earth, goodwill to men!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Hasten the time when, from ev&rsquo;ry clime,\nMen shall unite in the strains sublime:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Glory to God,\nGlory to God,\nGlory to God in the highest;\nPeace on earth, goodwill to men;\nPeace on earth, goodwill to men!",
						"type": "chorus"
					}
				]
