				"verses": [
					{
						"extra": false,
						"text": "Savior, may I learn to love thee,\nWalk the path that thou hast shown,\nPause to help and lift another,\nFinding strength beyond my own.\nSavior, may I learn to love thee&mdash;\nLord, I would follow thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Who am I to judge another\nWhen I walk imperfectly?\nIn the quiet heart is hidden\nSorrow that the eye can&rsquo;t see.\nWho am I to judge another?\nLord, I would follow thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "I would be my brother&rsquo;s keeper;\nI would learn the healer&rsquo;s art.\nTo the wounded and the weary\nI would show a gentle heart.\nI would be my brother&rsquo;s keeper&mdash;\nLord, I would follow thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Savior, may I love my brother\nAs I know thou lovest me,\nFind in thee my strength, my beacon,\nFor thy servant I would be.\nSavior, may I love my brother&mdash;\nLord, I would follow thee.",
						"type": "verse"
					}
				]
