				"verses": [
					{
						"extra": false,
						"text": "Let us all press on in the work of the Lord,\nThat when life is o&rsquo;er we may gain a reward;\nIn the fight for right let us wield a sword,\nThe mighty sword of truth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Fear not, though the enemy deride;\nCourage, for the Lord is on our side.\nWe will heed not what the wicked may say,\nBut the Lord alone we will obey.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "We will not retreat, though our numbers may be few\nWhen compared with the opposite host in view;\nBut an unseen pow&rsquo;r will aid me and you\nIn the glorious cause of truth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Fear not, though the enemy deride;\nCourage, for the Lord is on our side.\nWe will heed not what the wicked may say,\nBut the Lord alone we will obey.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "If we do what&rsquo;s right we have no need to fear,\nFor the Lord, our helper, will ever be near;\nIn the days of trial his Saints he will cheer,\nAnd prosper the cause of truth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Fear not, though the enemy deride;\nCourage, for the Lord is on our side.\nWe will heed not what the wicked may say,\nBut the Lord alone we will obey.",
						"type": "chorus"
					}
				]
