				"verses": [
					{
						"extra": false,
						"text": "This house we dedicate to thee,\nOur God, our fathers&rsquo; God.\nWilt thou accept and deign to bless\nThe path our feet have trod?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Wilt thou thy servants here inspire\nWhen in thy name they speak?\nAnd wilt thou bless each contrite soul\nWho here thy face doth seek?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Here may our sons and daughters come\nAnd find that peace which swells\nFrom grateful hearts, when touched by thee,\nWherein thy Spirit dwells!",
						"type": "verse"
					}
				]
