				"verses": [
					{
						"extra": false,
						"text": "The world has need of willing men\nWho wear the worker&rsquo;s seal.\nCome, help the good work move along;\nPut your shoulder to the wheel.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Put your shoulder to the wheel; push along,\nDo your duty with a heart full of song,\nWe all have work; let no one shirk.\nPut your shoulder to the wheel.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The Church has need of helping hands,\nAnd hearts that know and feel.\nThe work to do is here for you;\nPut your shoulder to the wheel.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Put your shoulder to the wheel; push along,\nDo your duty with a heart full of song,\nWe all have work; let no one shirk.\nPut your shoulder to the wheel.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Then don&rsquo;t stand idly looking on;\nThe fight with sin is real.\nIt will be long but must go on;\nPut your shoulder to the wheel.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Put your shoulder to the wheel; push along,\nDo your duty with a heart full of song,\nWe all have work; let no one shirk.\nPut your shoulder to the wheel.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Then work and watch and fight and pray\nWith all your might and zeal.\nPush ev&rsquo;ry worthy work along;\nPut your shoulder to the wheel.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Put your shoulder to the wheel; push along,\nDo your duty with a heart full of song,\nWe all have work; let no one shirk.\nPut your shoulder to the wheel.",
						"type": "chorus"
					}
				]
