				"verses": [
					{
						"extra": false,
						"text": "Jehovah, Lord of heav&rsquo;n and earth,\nThy word of truth proclaim!\nOh, may it spread from pole to pole,\nTill all shall know thy name;\nOh, may it spread from pole to pole,\nTill all shall know thy name.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "We long to see thy Church increase,\nThine own new kingdom grow,\nThat all the earth may live in peace,\nAnd heav&rsquo;n be seen below;\nThat all the earth may live in peace,\nAnd heav&rsquo;n be seen below.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Roll on thy work in all its pow&rsquo;r!\nThe distant nations bring!\nIn thy new kingdom may they stand,\nAnd own thee God and King;\nIn thy new kingdom may they stand,\nAnd own thee God and King.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "One general chorus then shall rise\nFrom men of ev&rsquo;ry tongue,\nAnd songs of joy salute the skies,\nBy ev&rsquo;ry nation sung;\nAnd songs of joy salute the skies,\nBy ev&rsquo;ry nation sung.",
						"type": "verse"
					}
				]
