				"verses": [
					{
						"extra": false,
						"text": "Oh, holy words of truth and love\nWe hear from day to day,\nRevealed to Saints from God above,\nTo guide in heaven&rsquo;s way.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Beautiful words of love\nComing from God above,\nHow sweet, how dear the words we hear!\nThey&rsquo;re beautiful words of love.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "They&rsquo;re from Apostles good and true,\nWhose names we all revere,\nWho daily teach us what to do\nIn words of love and cheer.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Beautiful words of love\nComing from God above,\nHow sweet, how dear the words we hear!\nThey&rsquo;re beautiful words of love.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "They&rsquo;re from the prophets God inspires,\nIn counsels oft withstood,\nReproving all our ill desires,\nCommending all that&rsquo;s good.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Beautiful words of love\nComing from God above,\nHow sweet, how dear the words we hear!\nThey&rsquo;re beautiful words of love.",
						"type": "chorus"
					},
					{
						"extra": true,
						"text": "And from each chosen one that speaks\nBy aid the Spirit gives,\nFor every sphere of life it seeks,\nFor everyone that lives.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Beautiful words of love\nComing from God above,\nHow sweet, how dear the words we hear!\nThey&rsquo;re beautiful words of love.",
						"type": "chorus"
					},
					{
						"extra": true,
						"text": "As gems of wisdom, pure and bright,\nThat glow with lustrous ray,\nWe&rsquo;ll seek to gain these words of light,\nTheir counsels to obey.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Beautiful words of love\nComing from God above,\nHow sweet, how dear the words we hear!\nThey&rsquo;re beautiful words of love.",
						"type": "chorus"
					}
				]
