				"verses": [
					{
						"extra": false,
						"text": "To Nephi, seer of olden time,\nA vision came from God,\nWherein the holy word sublime\nWas shown an iron rod.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hold to the rod, the iron rod;\n&rsquo;Tis strong, and bright, and true.\nThe iron rod is the word of God;\n&rsquo;Twill safely guide us through.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "While on our journey here below,\nBeneath temptation&rsquo;s pow&rsquo;r,\nThrough mists of darkness we must go,\nIn peril ev&rsquo;ry hour.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hold to the rod, the iron rod;\n&rsquo;Tis strong, and bright, and true.\nThe iron rod is the word of God;\n&rsquo;Twill safely guide us through.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "And when temptation&rsquo;s pow&rsquo;r is nigh,\nOur pathway clouded o&rsquo;er,\nUpon the rod we can rely,\nAnd heaven&rsquo;s aid implore.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Hold to the rod, the iron rod;\n&rsquo;Tis strong, and bright, and true.\nThe iron rod is the word of God;\n&rsquo;Twill safely guide us through.",
						"type": "chorus"
					},
					{
						"extra": true,
						"text": "And, hand o&rsquo;er hand, the rod along,\nThrough each succeeding day,\nWith earnest prayer and hopeful song,\nWe&rsquo;ll still pursue our way.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Hold to the rod, the iron rod;\n&rsquo;Tis strong, and bright, and true.\nThe iron rod is the word of God;\n&rsquo;Twill safely guide us through.",
						"type": "chorus"
					},
					{
						"extra": true,
						"text": "Afar we see the golden rest\nTo which the rod will guide,\nWhere, with the angels bright and blest,\nForever we&rsquo;ll abide.",
						"type": "verse"
					},
					{
						"extra": true,
						"text": "Hold to the rod, the iron rod;\n&rsquo;Tis strong, and bright, and true.\nThe iron rod is the word of God;\n&rsquo;Twill safely guide us through.",
						"type": "chorus"
					}
				]
