				"verses": [
					{
						"extra": false,
						"text": "As I search the holy scriptures,\nLoving Father of mankind,\nMay my heart be blessed with wisdom,\nAnd may knowledge fill my mind.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "As I search the holy scriptures,\nTouch my spirit, Lord, I pray.\nMay life&rsquo;s myst&rsquo;ries be unfolded\nAs I study day by day.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "As I search the holy scriptures,\nMay thy mercy be revealed.\nSoothe my troubled heart and spirit;\nMay my unseen wounds be healed.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "As I search the holy scriptures,\nHelp me ponder and obey.\nIn thy word is life eternal;\nMay thy light show me the way.",
						"type": "verse"
					}
				]
