				"verses": [
					{
						"extra": false,
						"text": "Welcome, welcome, Sabbath morning;\nNow we rest from ev&rsquo;ry care.\nWelcome, welcome is thy dawning,\nHoly Sabbath, day of prayer.\nLoving teachers kindly greet us\nAs we meet in Sunday School,\nWhere they labor hard to teach us\nBy the Savior&rsquo;s golden rule.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Welcome, welcome, Sabbath morning;\nNow we rest from ev&rsquo;ry care.\nWelcome, welcome is thy dawning,\nHoly Sabbath, day of prayer.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Hark! the Sabbath bells are ringing;\nHear the echoes all around.\nList! the merry children singing!\nWhat a pleasing, joyful sound!\nEv&rsquo;ry tender note entreats us,\nBids us come, nor longer stay.\nOn our way the music greets us;\nHasten, hasten, come away.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Welcome, welcome, Sabbath morning;\nNow we rest from ev&rsquo;ry care.\nWelcome, welcome is thy dawning,\nHoly Sabbath, day of prayer.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Here we bow in meek devotion;\nHere we sing God&rsquo;s holy praise.\nHere our hearts, with fond emotion,\nSeek to learn his holy ways.\nFrom the books of revelation\nWe are taught while yet in youth.\nWords of heav&rsquo;nly inspiration\nGuide us in the path of truth.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Welcome, welcome, Sabbath morning;\nNow we rest from ev&rsquo;ry care.\nWelcome, welcome is thy dawning,\nHoly Sabbath, day of prayer.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Here we meet with friends and neighbors;\nParents too are in the throng.\nWe are earnest in our labors.\nTo God&rsquo;s kingdom we belong.\nTrials make our faith grow stronger;\nTruth is nobler than a crown.\nWe will brave the tempest longer\nTho the world upon us frown.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Welcome, welcome, Sabbath morning;\nNow we rest from ev&rsquo;ry care.\nWelcome, welcome is thy dawning,\nHoly Sabbath, day of prayer.",
						"type": "chorus"
					}
				]
