				"verses": [
					{
						"extra": false,
						"text": "How gentle God&rsquo;s commands!\nHow kind his precepts are!\nCome, cast your burdens on the Lord\nAnd trust his constant care.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Beneath his watchful eye,\nHis Saints securely dwell;\nThat hand which bears all nature up\nShall guard his children well.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Why should this anxious load\nPress down your weary mind?\nHaste to your Heav&rsquo;nly Father&rsquo;s throne\nAnd sweet refreshment find.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "His goodness stands approved,\nUnchanged from day to day;\nI&rsquo;ll drop my burden at his feet\nAnd bear a song away.",
						"type": "verse"
					}
				]
