				"verses": [
					{
						"extra": false,
						"text": "Ye elders of Israel, come join now with me\nAnd seek out the righteous, where&rsquo;er they may be&mdash;\nIn desert, on mountain, on land, or on sea&mdash;\nAnd bring them to Zion, the pure and the free.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "O Babylon, O Babylon, we bid thee farewell;\nWe&rsquo;re going to the mountains of Ephraim to dwell.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The harvest is great, and the lab&rsquo;rers are few;\nBut if we&rsquo;re united, we all things can do.\nWe&rsquo;ll gather the wheat from the midst of the tares\nAnd bring them from bondage, from sorrows and snares.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "O Babylon, O Babylon, we bid thee farewell;\nWe&rsquo;re going to the mountains of Ephraim to dwell.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "We&rsquo;ll go to the poor, like our Captain of old,\nAnd visit the weary, the hungry, and cold;\nWe&rsquo;ll cheer up their hearts with the news that he bore\nAnd point them to Zion and life evermore.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "O Babylon, O Babylon, we bid thee farewell;\nWe&rsquo;re going to the mountains of Ephraim to dwell.",
						"type": "chorus"
					}
				]
