				"verses": [
					{
						"extra": false,
						"text": "Brethren, pow&rsquo;r by earthly standards\nComes by rank or wealth or sword;\nBut the pow&rsquo;r above all others\nIs the priesthood of our Lord.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "It is ours, the total armor&mdash;\nPriesthood held by Christ, our Lord&mdash;\nIf, as brethren, we are worthy\nOf the Spirit&rsquo;s whispered word.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Let us venture forth in freedom\nWith the priesthood as our guide&mdash;\nDeacons, teachers, priests, and elders,\nSeeking virtue side by side.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "May be sung without accompaniment as a round in unison by beginning every two measures.",
						"type": "other"
					}
				]
