				"verses": [
					{
						"extra": false,
						"text": "Lullaby, lullaby, my little one.\nLullaby, my child so dear.\nThy precious life has just begun;\nThy mother holds thee near.\nWhile Joseph watches through the night,\nA star reflects thy radiant light.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lullaby, lullaby, my little one.\nLullaby, my child so dear.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Thy gentle head shall wear a crown,\nFor thy Father is the King.\nThy tender hands, so tiny now,\nHave blessings great to bring.\nLet all creation join my song,\nFor peace and love this night are born.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lullaby, lullaby, my little one.\nLullaby, my child so dear.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "<em>Optional descant (verse 1)</em>\nWhile Joseph watches through the night,\nA star reflects thy light.\nLullaby, lullaby, my little one.\nLullaby, my child so dear.\n\n<em>Optional descant (verse 2)</em>\nLet all creation join my song,\nFor peace and love are born.\nLullaby, lullaby, my little one.\nLullaby, my child so dear.",
						"type": "other"
					}
				]
