				"verses": [
					{
						"extra": false,
						"text": "Oh, hush thee, my baby; a story I&rsquo;ll tell,\nHow little Lord Jesus on earth came to dwell;\nHow in a far country, &rsquo;way over the sea,\nWas born a wee baby, my dear one, like thee.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lullaby, baby, lullaby, dear.\nSleep, little baby; have nothing to fear.\nLullaby, baby, lullaby, dear.\nJesus will care for his little one here.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The story was told by the angels so bright,\nAs round them was shining a heavenly light.\nThe stars shone out brightly, but one led the way\nAnd stood o&rsquo;er the place where the dear baby lay.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lullaby, baby, lullaby, dear.\nSleep, little baby; have nothing to fear.\nLullaby, baby, lullaby, dear.\nJesus will care for his little one here.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "The shepherds here found him, as angels had said,\nThe poor little stranger, no crib for a bed.\nDown low in a manger so quiet he lay.\nThis little child Jesus, asleep on the hay.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Lullaby, baby, lullaby, dear.\nSleep, little baby; have nothing to fear.\nLullaby, baby, lullaby, dear.\nJesus will care for his little one here.",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "<em>Optional ostinato may be sung with the verse.</em>\nHush thee, my baby,&nbsp;Oh, hush&nbsp;<em>(etc.)</em>",
						"type": "other"
					}
				]
