				"verses": [
					{
						"extra": false,
						"text": "Have a very merry Christmas!\nScatter gladness ev’rywhere!\nSing the carols of the Christ child:\nLet your neighbors know you care.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Have a most exciting Christmas!\nRing a bell and trim a tree;\nStuff a stocking for a loved one;\nHang the holly joyously.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Have a happy fam’ly Christmas!\nGather all your loved ones near;\nTalk about the baby Jesus;\nWrap your hearts in love this year.",
						"type": "verse"
					}
				]
