				"verses": [
					{
						"extra": false,
						"text": "Jesus entered Jordan&rsquo;s waters\nWhen His work had just begun.\nGod the Father spoke from heaven:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "&ldquo;This is My Beloved Son. Hear Him!&rdquo;",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Nephites gazing into heaven\nSaw their white-robed Savior come.\nAnd they heard the Father witness:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "&ldquo;This is My Beloved Son. Hear Him!&rdquo;",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Joseph saw two glorious beings\nShining brighter than the sun.\nGod again presented Jesus:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "&ldquo;This is My Beloved Son. Hear Him!&rdquo;",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "As I read the scriptures daily&mdash;\nWords of Christ, the Holy One&mdash;\nIn my heart I&rsquo;ll hear God tell me:",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "&ldquo;This is My Beloved Son. Hear Him!&rdquo;",
						"type": "chorus"
					}
				]
