				"verses": [
					{
						"extra": false,
						"text": "Had I been a child when Jesus came\nUnto the Nephites that blessed day\nAnd showed them the wounds in his hands and his feet\nAnd knelt with them to pray.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Then he might have held me safe in his arms;\nHe might have looked in my eyes and said\nA word, with his blessing of kindness and love,\nHis hands placed upon my head.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Someday, when the Savior comes again,\nOh, how I hope that my heart will be\nAs pure as the hearts of the children that day\nWho gathered around his knee.",
						"type": "verse"
					}
				]
