				"verses": [
					{
						"extra": false,
						"text": "An angel came to Joseph Smith,\nAnd from the ground he took\nA sacred record hidden there,\nA precious, holy book.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "It tells of people long ago,\nLed by the Lord’s own hand,\nWho left their homes and crossed the sea\nTo reach a favored land.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The Nephites and the Lamanites,\nAnd all who came to dwell,\nHad peace when they obeyed the Lord,\nThe sacred records tell.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "And now I’ll read the sacred book,\nAnd then I’ll understand\nThat Heav’nly Father loves us all\nIn each and ev’ry land.",
						"type": "verse"
					}
				]
