				"verses": [
					{
						"extra": false,
						"text": "Jesus came to John the Baptist,\nIn Judea long ago,\nAnd was baptized by immersion\nIn the River Jordan’s flow.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "“To fulfill the law,” said Jesus,\nWhen the Baptist questioned why,\n“And to enter with my Father\nIn the kingdom up on high.”",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Now we know that we must also\nWitness faith in Jesus’ word,\nBe baptized to show obedience,\nAs was Jesus Christ, our Lord.",
						"type": "verse"
					}
				]
