				"verses": [
					{
						"extra": false,
						"text": "Let us gather in a circle\nAnd kneel in fam&rsquo;ly prayer\nTo thank our Heav&rsquo;nly Father\nFor the blessings we all share.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Let us thank him for our mealtime,\nFor clothes we daily wear,\nFor parents, home, and fam&rsquo;ly,\nFor his kind and loving care.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Oh, may we always serve him,\nIn thought and action too,\nAnd humbly kneel at prayer-time\nAs so many fam&rsquo;lies do.",
						"type": "verse"
					}
				]
