				"verses": [
					{
						"extra": false,
						"text": "Pioneer children were quick to obey,\nWalking along by the wagons all day,\nThen in the firelight, kneeling to pray,\nLittle pioneer children.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Carrying water and gathering wood,\nBuilding a campfire and cooking the food,\nLearning and helping the best that they could,\nLittle pioneer children.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Children today can be pioneers too,\nWilling and cheerful in all that we do,\nWalking our pathway with heaven in view,\nLittle pioneer children.",
						"type": "verse"
					}
				]
