				"verses": [
					{
						"extra": false,
						"text": "Oh, what do you do in the summertime, when all the world is green?\nDo you fish in a stream, or lazily dream on the banks as the clouds go by?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Is that what you do? So do I!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Oh, what do you do in the summertime, when all the world is green?\nDo you swim in a pool, to keep yourself cool, or swing in a tree up high?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Is that what you do? So do I!",
						"type": "chorus"
					},
					{
						"extra": false,
						"text": "Oh, what do you do in the summertime, when all the world is green?\nDo you march in parades, or drink lemonades, or count all the stars in the sky?",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Is that what you do? So do I!",
						"type": "chorus"
					}
				]
