				"uri": "/manual/childrens-songbook/the-wise-man-and-the-foolish-man",
				"verses": [
					{
						"extra": false,
						"text": "The wise man built his house upon the <em>rock,</em>\nThe wise man built his house upon the <em>rock,</em>\nThe wise man built his house upon the <em>rock,</em>\nAnd the <em>rains</em> came tumbling down.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The <em>rains</em> came down, and the <em>floods</em> came up,\nThe <em>rains</em> came down, and the <em>floods</em> came up,\nThe <em>rains</em> came down, and the <em>floods</em> came up,\nAnd the house on the <em>rock</em> stood still.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The foolish man built his house upon the <em>sand,</em>\nThe foolish man built his house upon the <em>sand,</em>\nThe foolish man built his house upon the <em>sand,</em>\nAnd the <em>rains</em> came tumbling down.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "The <em>rains</em> came down, and the <em>floods</em> came up,\nThe <em>rains</em> came down, and the <em>floods</em> came up,\nThe <em>rains</em> came down, and the <em>floods</em> came up,\nAnd the house on the <em>sand washed away.</em>",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Actions:\n<em>Rock:</em> Plant right fist solidly in palm of left hand.\n<em>Rains:</em> Let hands descend from high in the air.\n<em>Floods:</em> Raise hands with palms up.\n<em>Sand:</em> Wiggle fingers and move arms back and forth in front of body.\n<em>Washed away:</em> Wiggle fingers and move arms out away from the body.",
						"type": "other"
					}
				]
