				"uri": "/manual/childrens-songbook/have-a-very-happy-birthday",
				"verses": [
					{
						"extra": false,
						"text": "Have a very happy birthday\nFilled with sunshine ev&rsquo;rywhere!\nMay the year bring joy and gladness\nAnd the love of friends who care.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Have a most exciting birthday!\nShare a smile with someone new.\nSay a kind word to your neighbor;\nLet him know a happy you.",
						"type": "verse"
					},
					{
						"extra": false,
						"text": "Have a very special birthday!\nAdd a candle, blow it out,\nShare your cake, and share your friendship;\nThat&rsquo;s what birthdays are about.",
						"type": "verse"
					}
				]
